   = ">";\r\n sldBtnR.className      = btnStyle;\r\n sldBtnR.style.position = "absolute";\r\n //-------------------------------------------------------------------\r\n sldBar.className       = barStyle;\r\n sldBar.style.position  = "absolute";\r\n sldBar.style.top       = sldObjOffset.t;\r\n sldBar.style.height    = sldObjOffset.h;\r\n sldBar.style.left      = sldObjOffset.l;\r\n instance.fixed();\r\n //-------------------------------------------------------------------\r\n sldObj.onmousedown = function() {instance.handleObjBefore()};\r\n sldObj.onmouseup   = function() {instance.handleObjAfter()};\r\n //-------------------------------------------------------------------\r\n sldBtnL.onmousedown = function() {instance.handleBtnClick(''l'')};\r\n sldBtnR.onmousedown = function() {instance.handleBtnClick(''r'')};\r\n sldBtnL.onfocus     = function() {this.blur()};\r\n sldBtnR.onfocus     = function() {this.blur()};\r\n //-------------------------------------------------------------------\r\n sldBar.onmousedown = function() {instance.handleSldDragStart()};\r\n sldBar.onmousemove = function() {instance.handleSldDrag()};\r\n sldBar.onmouseup   = function() {instance.handleSldDragEnd()};\r\n}};\r\nneverSliderBar.prototype.fixed=function() { with(this)\r\n{\r\n sldBarOffset = Offset(sldBar);\r\n sldObjOffset = Offset(sldObj);\r\n\r\n var sldBtnLOffset      = Offset(sldBtnL);\r\n sldBtnL.style.left     = sldObjOffset.l-sldBtnLOffset.w;\r\n sldBtnL.style.top      = sldObjOffset.t;\r\n sldBtnL.style.height   = sldObjOffset.h;\r\n //-------------------------------------------------------------------\r\n sldBtnR.style.left     = sldObjOffset.l+sldObjOffset.w;\r\n sldBtnR.style.top      = sldObjOffset.t;\r\n sldBtnR.style.height   = sldObjOffset.h;\r\n //-------------------------------------------------------------------\r\n sldBar.style.top       = sldObjOffset.t;\r\n sldBar.style.height    = sldObjOffset.h;\r\n //-------------------------------------------------------------------\r\n var p = sldBarOffset.l;\r\n if (p < sldObjOffset.l) sldBar.style.left=sldObjOffset.l;\r\n var w = sldObjOffset.l+sldObjOffset.w-sldBarOffset.w;\r\n if (p > w) sldBar.style.left = w;\r\n window.setTimeout(function(){instance.fixed()},10)\r\n}};\r\nneverSliderBar.prototype.applyArg=function() { with(this)\r\n{\r\n if (typeof(callback)==''string'') callback=eval(callback);\r\n if (typeof(callback)==''function'') {\r\n  var callbackArguments = [];\r\n  for(var i=0; i<callbackArg.length; i++)\r\n  callbackArguments[i] = callbackArg[i];\r\n  callbackArguments.push(instance.getSldPoint());\r\n  callback.apply(this,callbackArguments);\r\n } else { return; }\r\n}};\r\nneverSliderBar.prototype.handleObjBefore=function() { with(this)\r\n{\r\n\r\n}};\r\nneverSliderBar.prototype.handleObjAfter=function() { with(this)\r\n{\r\n\r\n}};\r\nneverSliderBar.prototype.handleBtnClick=function(direction) { with(this)\r\n{\r\n direction = direction.toLowerCase();\r\n sldPoint=instance.getSldPoint();\r\n if(direction == ''l'') {\r\n  instance.setSldPoint(this.sldPoint-sldIncrement);\r\n }\r\n else if (direction==''r'') {\r\n  instance.setSldPoint(this.sldPoint+sldIncrement);\r\n }\r\n else {\r\n  return alert(''not valid argument '' +direction);\r\n }\r\n instance.applyArg();\r\n instance.getSldPoint();\r\n}};\r\nneverSliderBar.prototype.handleSldDragStart=function() { with(this)\r\n{\r\n sldBar.setCapture();\r\n sldMoved = true;\r\n sldBar.onlosecapture = function(){sldMoved=false;};\r\n sldPoint = event.clientX-sldBarOffset.l;\r\n}};\r\nneverSliderBar.prototype.handleSldDrag=function() { with(this)\r\n{\r\n if(!sldMoved) return;\r\n var p = event.clientX-sldPoint;\r\n if (p <= sldObjOffset.l) {\r\n  sldBar.style.left = sldObjOffset.l;\r\n }\r\n else if (p >= (sldObjOffset.l+sldObjOffset.w-sldBarOffset.w)) {\r\n  sldBar.style.left = sldObjOffset.l+sldObjOffset.w-sldBarOffset.w;\r\n }\r\n else sldBar.style.left = p;\r\n instance.applyArg();\r\n instance.getSldPoint();\r\n}};\r\nneverSliderBar.prototype.handleSldDragEnd=function() { with(this)\r\n{\r\n sldBar.releaseCapture();\r\n sldMoved=false;\r\n}};\r\nfunction Offset(e) {\r\n var t = e.offsetTop;\r\n var l = e.offsetLeft;\r\n var w = e.offsetWidth;\r\n var h = e.offsetHeight;\r\n while(e=e.offsetParent) {\r\n  t+=e.offsetTop;\r\n  l+=e.offsetLeft;\r\n }\r\n return { t:t, l:l, w:w, h:h }\r\n}\r\n\r\n\r\n\r\n\r\n\r\nfunction callback(s) {\r\n var $=document.getElementById;\r\n var pos=bar.getSldPoint()*1000;\r\n 	//alert("您拖动到了："+pos);\r\n\r\n VGPDemo.SetCurrentPosition(pos);\r\n}\r\n\r\nfunction volcallback(s) {\r\n 	var $=document.getElementById;\r\n 	var pos=volbar.getSldPoint();\r\n	VGPDemo.Volume=pos;\r\n}\r\n\r\nfunction play(){\r\n    VGPDemo.Play();\r\n}\r\n\r\nfunction stop(){\r\n  VGPDemo.Stop();\r\n   bar.setSldPoint(0);\r\n\r\n}\r\n\r\nfunction ShowVideo(){\r\n   VGPDemo.ShowVideo(true );\r\n}\r\n\r\nfunction hideVideo(){\r\n   VGPDemo.ShowVideo(false);\r\n\r\n}\r\n\r\nfunction FullScreen(){\r\n   VGPDemo.SetFullScreen( 1);\r\n}\r\n\r\n\r\nfunction getCurrPos(){\r\n		//alert("getCurrPos,VGPDemo＝"+VGPDemo);\r\n	var n=VGPDemo.GetCurrentPosition();		//毫秒\r\n		//alert(n);\r\n	n=parseInt(n /1000);\r\n\r\n\r\n\r\n	//n=parseInt(n);\r\n	if(n<=0){\r\n		return;\r\n	}\r\n\r\n	var n2=bar.getSldPoint();\r\n	if(n<=n2){\r\n		return;\r\n	}\r\n\r\n	document.getElementById("currpos").innerText="当前进度："+n;\r\n	bar.setSldPoint(n);\r\n\r\n}\r\nvar movie;\r\nfunction changeMuteState(){\r\n	var b=document.getElementById("chkMute").checked;\r\n\r\n	var n;\r\n	if(b){\r\n		//mute\r\n		n=1;\r\n\r\n	}else{\r\n		n=0;\r\n	}\r\n	VGPDemo.Mute=n;\r\n}\r\n\r\n\r\n\r\nfunction startTimer(){\r\n	window.setInterval(getCurrPos,1000);\r\n}\r\n\r\ndocument.onreadystatechange = function() {\r\n  	if(document.readyState == "complete") {\r\n			bar=new neverSliderBar("currposbar",callback,'' <b>当前进度</b> '');\r\n			volbar=new neverSliderBar("currvol",volcallback,'' <b>当前音量</b> '');\r\n			PlayerInit();\r\n	}\r\n\r\n}\r\nfunction PlayerInit()\r\n{\r\n    var node ="{$skyuc_src}";\r\n    movie=node;\r\n	VGPDemo.URL =node;\r\n}\r\n\r\nfunction RestorePlayerWindow(iWidth, iHeight)\r\n{\r\n	window.resizeTo(iWidth, iHeight);\r\n	availWidth	= screen.Width;\r\n	availHeight	= screen.availHeight;\r\n	iTmpPix		= availWidth - iWidth;\r\n	iWinLeft	= iTmpPix / 2;\r\n	iTmpPix		= availHeight - iHeight;\r\n	iWinHeight	= iTmpPix / 2;\r\n	window.moveTo(iWinLeft, iWinHeight);\r\n}\r\n\r\n\r\n//检测播放器\r\nfunction CheckPlayer()\r\n{\r\n	try\r\n	{\r\n		var oTest = new ActiveXObject("VGPlayer.WebVGPlayer.1");\r\n	}\r\n	catch(e)\r\n	{\r\n		if(window.opener == undefined)\r\n		{\r\n			window.alert("首次点播将安装播放器插件...");\r\n			parent.window.location.href = "http://www.taichu.org/WebPlayer2010.exe";\r\n		}\r\n		else\r\n		{\r\n			window.opener.alert("首次点播将安装播放器插件...");\r\n			window.opener.parent.location.href = "http://www.taichu.org/WebPlayer2010.exe";\r\n			self.close();\r\n		}\r\n		return false;\r\n	}\r\n	delete oTest;\r\n\r\n	PlayerInit();\r\n	//RestorePlayerWindow(334, 352);\r\n	return true;\r\n}\r\n\r\n\r\n</script>\r\n<SCRIPT FOR="VGPDemo" EVENT="Complement(nStatus)">\r\n	//当节目有下一集时自动播放下一集。若没有的话。节目放完后就终止;\r\n	var $=document.getElementById;\r\n		if(nStatus==6){\r\n\r\n			 var nexturl = "{$skyuc_nextpage}";\r\n\r\n			if(nexturl != '''')\r\n			{\r\n				window.location.href=nexturl;\r\n			}else{\r\n				$("play").value=0;\r\n				$("bt_2").className=''Pause'';\r\n				VGPDemo.stop();\r\n				$("play_status").innerHTML=''停止：'';\r\n				$("tool_block").style.marginLeft=''0px'';\r\n			}\r\n\r\n		}\r\n\r\n</SCRIPT>\r\n<SCRIPT FOR="VGPDemo" EVENT="Ready">\r\n	//播放器初始大小信息\r\n			var iWidth=VGPDemo.GetVideoWidth();\r\n			var iHeight = VGPDemo.GetVideoHeight();\r\n			document.getElementById("screen").innerHTML="分辨率："+iWidth+"X"+iHeight;\r\n//			self.resizeTo(400,320);\r\n//			if(iWidth){\r\n//				RestorePlayerWindow(500, 375);\r\n//				//框架里总宽高\r\n//			}else{\r\n//				document.getElementById("VGPDemo_table").style.display=''none'';\r\n//				RestorePlayerWindow(420,110);\r\n//\r\n//			}\r\n</SCRIPT>\r\n\r\n\r\n\r\n<SCRIPT FOR="VGPDemo" EVENT="SizeChange(newSize)">\r\n	//播放器页面改变大小时播放器大小\r\n	var h=VGPDemo.GetVideoHeight()*newSize;\r\n	var w=VGPDemo.GetVideoWidth()*newSize;\r\n	document.getElementById("VGPDemo").height=h;\r\n	document.getElementById("VGPDemo").width=w;\r\n	self.resizeTo(w+300,h+110);\r\n</SCRIPT>\r\n\r\n<SCRIPT FOR="VGPDemo" EVENT="AUDIOChange(newAudioMode)">\r\n	//调节声道时页面的图片显示\r\n	if(newAudioMode==0){\r\n		VGPDemo.Balance	=0;\r\n		var p_m=0;\r\n		$("paly_t").style.marginLeft=p_m;\r\n		//VGPDemo.Mute=false;\r\n	}else if(newAudioMode==50){\r\n		VGPDemo.Balance	=50;\r\n		var p_m=8;\r\n		$("paly_t").style.marginLeft=p_m;\r\n		//VGPDemo.Mute=false;\r\n	}else if(newAudioMode==100){\r\n		VGPDemo.Balance=100;\r\n		var p_m=16;\r\n		$("paly_t").style.marginLeft=p_m;\r\n		//VGPDemo.Mute=false;\r\n	}else if(newAudioMode==200){\r\n		if(VGPDemo.Mute){\r\n			VGPDemo.Mute=false;\r\n		}else{\r\n			VGPDemo.Mute=true;\r\n		}\r\n	}\r\n</SCRIPT>\r\n\r\n<div id="log"></div>\r\n\r\n<script language="javascript">\r\n		//页页宽度改时显示的栏目，一般情况下不需要更改\r\n			//window.onresize=chkwindow;\r\n			var $=document.getElementById;\r\n\r\n			function chkwindow()\r\n			{\r\n				var w=document.body.clientWidth;\r\n				var windows=$("windows");\r\n				var logowidth=w-350;  //LOGO宽度\r\n				if(logowidth>0){\r\n					$("play_logo").style.width=logowidth;\r\n					$("play_logo").style.display=''block'';\r\n				}else{\r\n					$("play_logo").style.display=''none'';\r\n				}\r\n				if(w<476){  //隐藏声道\r\n					$("play_track").style.display=''none'';\r\n					$("screen").style.display=''none'';\r\n\r\n				}else{\r\n					$("play_track").style.display=''block'';\r\n					$("screen").style.display=''block'';\r\n				}\r\n\r\n				if(w<380){\r\n					$("sound").style.display=''none'';\r\n				}else{\r\n					$("sound").style.display=''block'';\r\n				}\r\n\r\n				if (w<283){\r\n					$("play_sound").style.display=''none'';\r\n				}else{\r\n					$("play_sound").style.display=''block'';\r\n				}\r\n				if(w>=100){\r\n					v=99;\r\n				}else{\r\n					v=w;\r\n				}\r\n				var w=document.body.offsetWidth;\r\n				var h=document.body.offsetHeight;\r\n\r\n				document.getElementById("VGPDemo").height=h-81;\r\n				document.getElementById("VGPDemo").width=w-110;\r\n			}\r\n\r\n		//初始播放进度条控制\r\n	window.onload = function ()\r\n	{\r\n		$("tool_bg").onmousedown=function(){\r\n			document.attachEvent("onmousemove",tool_move);\r\n  			document.attachEvent("onmouseup",tool_up);\r\n		}\r\n\r\n		function tool_move()\r\n		{\r\n\r\n				var w=document.body.clientWidth;  //播放器WIDTH\r\n				var tool=parseInt(w*0.632);      //长度\r\n				var win_w=event.x-30;\r\n\r\n			if(event.x<tool)\r\n			{\r\n\r\n				$("tool_block").style.marginLeft=win_w;\r\n			}\r\n\r\n		}\r\n		function tool_up()\r\n		{\r\n			document.detachEvent("onmousemove",tool_move);   //注销TOOL_MOVE\r\n			document.detachEvent("onmouseup",tool_up);      //注销TOOL_UP\r\n		}\r\n\r\n\r\n\r\n		$("tool_bg").onmouseup=function(){\r\n			var win_w=event.x-30;\r\n			var w=document.body.clientWidth;  //播放器WIDTH\r\n			var tool=parseInt(w*0.632);      //长度\r\n			var t=event.x/tool;\r\n			var mt=VGPDemo.GetDuration;   			//MOVIE TIME\r\n			if(win_w<0){win_w=0}\r\n			if(event.x<tool){\r\n				$("tool_block").style.marginLeft=win_w;\r\n			}\r\n			 if(t>1){t=1}\r\n			pos=mt*t;\r\n 			VGPDemo.SetCurrentPosition(pos);\r\n		}\r\n\r\n		//控制音量拖动图标大小改变\r\n		$("paly_s_bg").onmouseup=function()\r\n		{\r\n			var m_x=event.clientX;  //鼠标X\r\n			var w_w=document.body.clientWidth;\r\n			var p_w=w_w-150;    //paly_s的起点\r\n			var p_m=m_x-p_w;   //距离起点的距离\r\n			if(p_m>58)\r\n			{\r\n				p_m=64;\r\n			}\r\n			p_m=p_m-9;\r\n			if (p_m<0){p_m=-2}\r\n			$("paly_s").style.marginLeft=p_m;\r\n			var Volume=parseInt(p_m*2);\r\n			if(Volume<0){Volume=0}\r\n			if(Volume>100){Volume=100}\r\n			VGPDemo.Volume=Volume;\r\n			$("sound").innerHTML=" 音量："+VGPDemo.Volume+"%";\r\n		}\r\n\r\n		$("paly_t_bg").onmouseup=function(