).style.marginLeft=p_m;\r\n		//VGPDemo.Mute=false;\r\n	}else if\r\n\r\n(newAudioMode==100){\r\n		VGPDemo.Balance=100;\r\n		var p_m=16;\r\n\r\n\r\n		$("paly_t").style.marginLeft=p_m;\r\n		//VGPDemo.Mute=false;\r\n	\r\n\r\n}else if(newAudioMode==200){\r\n		if(VGPDemo.Mute){\r\n			\r\n\r\nVGPDemo.Mute=false;\r\n		}else{\r\n			VGPDemo.Mute=true;\r\n		\r\n\r\n}\r\n	}\r\n</SCRIPT>\r\n\r\n<div id="log"></div>\r\n\r\n<script language="javascript">\r\n	\r\n\r\n	//页页宽度改时显示的栏目，一般情况下不需要更改\r\n			\r\n\r\n//window.onresize=chkwindow;\r\n			var $=document.getElementById;\r\n\r\n\r\n\r\n			function chkwindow()\r\n			{\r\n			\r\n\r\n	var w=document.body.clientWidth;\r\n				var windows=\r\n\r\n$("windows");\r\n				var logowidth=w-350;  //LOGO宽度\r\n		\r\n\r\n		if(logowidth>0){\r\n					\r\n\r\n$("play_logo").style.width=logowidth;\r\n					\r\n\r\n$("play_logo").style.display=''block'';\r\n				}else{\r\n		\r\n\r\n			$("play_logo").style.display=''none'';\r\n				\r\n\r\n}\r\n				if(w<476){  //隐藏声道\r\n					\r\n\r\n$("play_track").style.display=''none'';\r\n					\r\n\r\n$("screen").style.display=''none'';\r\n\r\n				}else{\r\n		\r\n\r\n			$("play_track").style.display=''block'';\r\n				\r\n\r\n	$("screen").style.display=''block'';\r\n				}\r\n\r\n	\r\n\r\n			if(w<380){\r\n					\r\n\r\n$("sound").style.display=''none'';\r\n				}else{\r\n			\r\n\r\n		$("sound").style.display=''block'';\r\n				}\r\n\r\n	\r\n\r\n			if (w<283){\r\n					\r\n\r\n$("play_sound").style.display=''none'';\r\n				}else{\r\n		\r\n\r\n			$("play_sound").style.display=''block'';\r\n				\r\n\r\n}\r\n				if(w>=100){\r\n					v=99;\r\n	\r\n\r\n			}else{\r\n					v=w;\r\n		\r\n\r\n		}\r\n				var w=document.body.offsetWidth;\r\n		\r\n\r\n		var h=document.body.offsetHeight;\r\n\r\n				\r\n\r\ndocument.getElementById("VGPDemo").height=h-81;\r\n				\r\n\r\ndocument.getElementById("VGPDemo").width=w-110;\r\n			}\r\n\r\n		\r\n\r\n//初始播放进度条控制\r\n	window.onload = function ()\r\n	{\r\n		\r\n\r\n$("tool_bg").onmousedown=function(){\r\n			document.attachEvent\r\n\r\n("onmousemove",tool_move);\r\n  			document.attachEvent\r\n\r\n("onmouseup",tool_up);\r\n		}\r\n\r\n		function tool_move()\r\n	\r\n\r\n	{\r\n\r\n				var w=document.body.clientWidth;  //播放器\r\n\r\nWIDTH\r\n				var tool=parseInt(w*0.632);      //长度\r\n		\r\n\r\n		var win_w=event.x-30;\r\n\r\n			if(event.x<tool)\r\n		\r\n\r\n	{\r\n\r\n				$("tool_block").style.marginLeft=win_w;\r\n	\r\n\r\n		}\r\n\r\n		}\r\n		function tool_up()\r\n		{\r\r\n\r\n\n			document.detachEvent("onmousemove",tool_move);   //注销TOOL_MOVE\r\n	\r\n\r\n		document.detachEvent("onmouseup",tool_up);      //注销TOOL_UP\r\n		\r\n\r\n}\r\n\r\n\r\n\r\n		$("tool_bg").onmouseup=function(){\r\n			var \r\n\r\nwin_w=event.x-30;\r\n			var w=document.body.clientWidth;  //播放器WIDTH\r\n	\r\n\r\n		var tool=parseInt(w*0.632);      //长度\r\n			var \r\n\r\nt=event.x/tool;\r\n			var mt=VGPDemo.GetDuration;   			\r\n\r\n//MOVIE TIME\r\n			if(win_w<0){win_w=0}\r\n			if\r\n\r\n(event.x<tool){\r\n				$("tool_block").style.marginLeft=win_w;\r\n	\r\n\r\n		}\r\n			 if(t>1){t=1}\r\n			pos=mt*t;\r\r\n\r\n\n 			VGPDemo.SetCurrentPosition(pos);\r\n		}\r\n\r\n		\r\n\r\n//控制音量拖动图标大小改变\r\n		$("paly_s_bg").onmouseup=function()\r\n		{\r\r\n\r\n\n			var m_x=event.clientX;  //鼠标X\r\n			var \r\n\r\nw_w=document.body.clientWidth;\r\n			var p_w=w_w-150;    //paly_s的起点\r\n\r\n\r\n			var p_m=m_x-p_w;   //距离起点的距离\r\n			if\r\n\r\n(p_m>58)\r\n			{\r\n				p_m=64;\r\n			\r\n\r\n}\r\n			p_m=p_m-9;\r\n			if (p_m<0){p_m=-2}\r\n			\r\n\r\n$("paly_s").style.marginLeft=p_m;\r\n			var Volume=parseInt(p_m*2);\r\n		\r\n\r\n	if(Volume<0){Volume=0}\r\n			if(Volume>100){Volume=100}\r\n		\r\n\r\n	VGPDemo.Volume=Volume;\r\n			$("sound").innerHTML=" 音\r\n\r\n量："+VGPDemo.Volume+"%";\r\n		}\r\n\r\n		\r\n\r\n$("paly_t_bg").onmouseup=function()\r\n		{\r\n			var \r\n\r\nm_x=event.clientX;  //鼠标X\r\n			var w_w=document.body.clientWidth;\r\n		\r\n\r\n	var p_w=w_w-56;    //paly_s的起点\r\n			var p_m=m_x-p_w;   //距离起\r\n\r\n点的距离\r\n			if(p_m>53){\r\n				p_m=53;\r\n		\r\n\r\n	}\r\n			p_m=p_m;\r\n			if (p_m<0){p_m=0}\r\n		\r\n\r\n	if(p_m<8){\r\n				VGPDemo.Balance	=0;\r\n				\r\n\r\np_m=0;\r\n			}else if(p_m>20){\r\n				\r\n\r\nVGPDemo.Balance=100;\r\n				p_m=34;\r\n			}\r\n\r\nelse{\r\n				VGPDemo.Balance=50;\r\n				\r\n\r\np_m=17;\r\n			}\r\n			$("paly_t").style.marginLeft=p_m;\r\r\n\r\n\n		}\r\n		$("bt_5").onmouseup=function()\r\n		{\r\n		\r\n\r\n	if(VGPDemo.Mute){\r\n				VGPDemo.Mute=false;\r\n			\r\n\r\n}else{\r\n				VGPDemo.Mute=true;\r\n			}\r\n\r\n	\r\n\r\n	}\r\n\r\n		//暂停与播放\r\n		$("bt_2").onclick=function\r\n\r\n()\r\n		{\r\n			var p=$(''play'').value;\r\n\r\n			\r\n\r\nif(p==1){\r\n				//Stop();\r\n				\r\n\r\n$(''play'').value=0;\r\n				$("bt_2").className=''Pause'';\r\n	\r\n\r\n			VGPDemo.Pause();\r\n				\r\n\r\n$("play_status").innerHTML=''暂停：'';\r\n\r\n			}else{\r\n			\r\n\r\n	$(''play'').value=1;\r\n				\r\n\r\n$("bt_2").className=''play'';\r\n				play();\r\n			\r\n\r\n	$("play_status").innerHTML=''正在播放：'';\r\n			}\r\n\r\n		\r\n\r\n}\r\n		$("bt_3").onclick=function()\r\n		{\r\n			\r\n\r\n$(''play'').value=0;\r\n			$("bt_2").className=''Pause'';\r\n		\r\n\r\n	VGPDemo.stop();\r\n			$("play_status").innerHTML=''停止：'';\r\n	\r\n\r\n		$("tool_block").style.marginLeft=''0px'';\r\n		}\r\n\r\n		\r\n\r\n//换肤\r\n		$("skin").onmouseover=function()\r\n		{\r\n			\r\n\r\n$("skin_menu").style.display=''block'';\r\n		}\r\n		\r\n\r\n$("skin").onmouseout=function()\r\n		{\r\n			\r\n\r\n$("skin_menu").style.display=''none'';\r\n		}\r\n\r\n		//上一集\r\n	\r\n\r\n	$("bt_1").onclick=function()\r\n		{\r\n		   var \r\n\r\nprevurl="{$skyuc_prevpage}";\r\n		   if(prevurl != '''')\r\n		   \r\n\r\n{\r\n			   window.location.href=prevurl;\r\n		   }\r\n		\r\n\r\n}\r\n\r\n		//下一集\r\n		$("bt_4").onclick=function()\r\n		\r\n\r\n{\r\n	       var nexturl="{$skyuc_nextpage}";\r\n		   if(nexturl != '''')\r\n	\r\n\r\n	   {\r\n			 window.location.href=nexturl;\r\n		   \r\n\r\n}\r\n\r\n		}\r\n\r\n\r\n	}\r\n\r\n		//更改皮肤函数及装载皮肤函数 \r\n\r\n参数t